<template>
  <div class="content-wrapper">
    <section class="section-box">
      <div class="container">
        <div class="row d-flex justify-content-center text-center align-items-center">
          <div class="col-md-9 col-lg-7">
            <div class="c-hero__img">
              <!-- TODO: Change image used to represen the preview on the registrtaion intro page -->
              <img class="c-hero__laptop img-fluid" style="max-height:150px;" src="data:image/svg+xml;utf8;base64," alt="Welcome to iBorrow">
            </div>

          </div>

          <div class="col-md-9 col-lg-7 mt-5">
            <h1 class="text-center">Registration Successful</h1>
            <p>Hi {{user.firstName}}, Your account has been succesfully set up on iBorrow, however to make things more formal, please complete the steps below to continue using your account</p>
            <h3>Get Started with these steps</h3>
            <hr/>
            <card-caption></card-caption>
          </div>
        </div>

      </div>
    </section>
  </div>
</template>
<script>
import axios from 'axios'
import CardCaption from './Captions'
export default {
  name: 'welcome',
  data () {
    return {
      user: []
    }
  },
  created () {
    // invoke the method to check for user
    this.getUser()
  },
  methods: {
    /** Check the newly registered user information using the userID params in the URL,
     * if you can get the user then show the user the page if not then take the user out of this page
     * */
    async getUser () {
      const userID = this.$route.params.userId
      const { data } = await axios.get('/api/auth/check/' + userID)
      if (data.status === 400) {
        this.$router.push({ name: 'error.registration' })
      }
      this.user = data.user
    }
  },
  components: {
    CardCaption
  }
}
</script>
